    private void BotaoAdicaoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotaoAdicaoActionPerformed
        Adicao soma = new Adicao();
        num1=Integer.parseInt(CampoValor1.getText());
        num2=Integer.parseInt(CampoValor2.getText());
        CampoResultado.setText(String.valueOf(soma.calcular(num1,num2)));
         try{
        File f = new File("src\\Audios\\AdicaoAudio.wav");
        Clip clip = AudioSystem.getClip();
        AudioInputStream ais = AudioSystem.getAudioInputStream(f);
        clip.open(ais);
        clip.start();  
    }catch(Exception exception){
        System.out.println("Falha ao iniciar arquivo");
    }
        
    }//GEN-LAST:event_BotaoAdicaoActionPerformed

