    private void BotaoDivisaoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotaoDivisaoActionPerformed
        
        Divisao div = new Divisao();
        num1=Integer.parseInt(CampoValor1.getText());
        num2=Integer.parseInt(CampoValor2.getText());
        try{
        res=div.calcular(num1,num2);        
       }
        catch(ArithmeticException obj){
        JOptionPane.showMessageDialog(null,"Erro divisao por zero");
        this.dispose();
        }
       CampoResultado.setText(String.valueOf(res));
       
        try{
        File f = new File("src\\Audios\\DivisaoAudio.wav");
        Clip clip = AudioSystem.getClip();
        AudioInputStream ais = AudioSystem.getAudioInputStream(f);
        clip.open(ais);
        clip.start();  
    }catch(Exception exception){
        System.out.println("Falha ao iniciar arquivo");
    }
      
    }//GEN-LAST:event_BotaoDivisaoActionPerformed

